{% block title %}Post index{% endblock %}

{% block body %}

<div class=\"container\">
    <h1>Post index</h1>
    <table class=\"table table-striped\" id=\"myTable\">
    <div class=\"d-flex justify-content-md-end mb-3\">
        <a class=\"btn btn-primary\" href=\"{{ path('post_new') }}\"><i class=\"fas fa-plus-square\"></i> Nuevo Post</a>
    </div>
        <thead>
            <tr>
                <th>Id</th>
                <th>Titulo</th>
                <th>Fecha</th>
                <th>Texto</th>
                <th>Imagen</th>
                <th>actions</th>
            </tr>
        </thead>
        <tbody>
        {% for post in posts %}
            <tr>
                <td>{{ post.id }}</td>
                <td>{{ post.titulo }}</td>
                <td>{{ post.fecha ? post.fecha|date('Y-m-d') : '' }}</td>
                <td>{{ post.texto }}</td>
                <td>{{ post.imagen }}</td>
                <td>
                    <a  class=\"btn btn-primary\" href=\"{{ path('post_show', {'id': post.id}) }}\"><i class=\"fas fa-eye\"></i></a>
                    <a class=\"btn btn-info\" href=\"{{ path('post_edit', {'id': post.id}) }}\"><i class=\"fas fa-pencil-alt\"></i></a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"6\">No hay registros</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    

</div>
{% endblock %}
